import React, { useState } from 'react';
import axios from 'axios';

function FamilyMembers() {
  const [familyMembers, setFamilyMembers] = useState([]);

  function handleSubmit(event) {
    event.preventDefault();
    const form = event.target;
    const member = {
      name: form.name.value,
      surname: form.surname.value,
      age: form.age.value,
      relationship: form.relationship.value
    };

    axios.post('http://localhost:3001/create', member)
      .then((response) => {
        console.log(response);
        setFamilyMembers([...familyMembers, response.data]);
      })
      .catch((error) => {
        console.log(error);
      }
    );
    
    const [familyMembers, setFamilyMembers] = useState([]);

      useEffect(() => {
       axios.get('http://localhost:3001/create')
        .then((response) => {
        setFamilyMembers(response.data);
        })
        .catch((error) => {
         console.log(error);
        });
      }, 
      []
    );
  }

  return (
    <div className="telostranky">
      <center>
      <div className='formtabulka'>
        <form onSubmit={handleSubmit}>
          <label>
            Meno:
            <input type="text" name="name" required />
          </label>
          <label>
            Priezvisko:
            <input type="text" name="surname" required />
          </label>
          <label>
            Vek:
            <input type="number" name="age" required />
          </label>
          <label>
            Príbuzenský vzťah:
            <select name="relationship" required>
              <option value="otec">Otec</option>
              <option value="matka">Matka</option>
              <option value="brat">Brat</option>
              <option value="sestra">Sestra</option>
              <option value="syn">Syn</option>
              <option value="dcéra">Dcéra</option>
            </select>
          </label>
          <button type="submit">Pridať člena</button>
        </form>
      </div>
      <div>
        <button type="submit">Zobraziť členov</button>
      </div>
      </center>
    </div>
  );
}

export default FamilyMembers;
