import React, { useState } from 'react';
import axios from 'axios';

function vypisovanie() {
  const [familyMembers, setFamilyMembers] = useState([]);
  
  function handleClick() {
    axios.get('http://localhost:3001/get')
      .then((response) => {
        setFamilyMembers(response.data);
      })
      .catch((error) => {
        console.log(error);
      })
  };

return (
    <center>
        <div>
        <button type="button" onClick={handleClick}>Zobraziť členov</button>
        <table>
            <thead>
                <tr>
                    <th>Meno</th>
                    <th>Priezvisko</th>
                    <th>Vek</th>
                    <th>Príbuzenský vzťah</th>
                </tr>
            </thead>
            <tbody>
                {familyMembers.map((member, index) => (
                <tr key={index}>
                    <td>{member.meno}</td>
                    <td>{member.priezvisko}</td>
                    <td>{member.vek}</td>
                    <td>{member.pribuzenstvo}</td>
                </tr>
                ))}
            </tbody>
        </table>
        </div>
    </center>
    );
}

export default vypisovanie;
